import assert from 'assert'
import Plugin from '../plugin'

describe('Plugin', function() {
  let trackomaticMock

  beforeEach(() => {
    trackomaticMock = {
      track: sinon.stub()
    }
  })

  afterEach(() => {
    trackomaticMock = null
  })

  it('stores a reference to the instance of Trackomatic as this.__trackomatic__', function() {
    let plugin = new Plugin(trackomaticMock)

    assert.equal(plugin.__trackomatic__, trackomaticMock)
  })

  it('provides a method that proxies Trackomatic.track() as this.track()', function() {
    let plugin = new Plugin(trackomaticMock)
    let params = {}

    plugin.track(params)

    assert(trackomaticMock.track.calledWith(params))
  })
})

import assert from 'assert'
import Trackomatic from '../trackomatic'

describe('Trackomatic', function() {
  let trackerMock = null

  beforeEach(() => {
    trackerMock = {
      get: sinon.stub()
    }
  })

  it('stores the provided tracker', function() {
    let tracker = {}
    let instance = new Trackomatic(tracker)

    assert.equal(instance.tracker, tracker)
  })

  it('extends default config settings with provided options', function() {
    let options = { redirectDelay: 1 }
    let instance = new Trackomatic({}, options)

    assert.equal(instance.options.redirectDelay, 1)
  })

  it('converts configured `files` and `networks` options into RegExp objects', function() {
    let options = { files: [ '.pdf', '.swf' ] }
    let instance = new Trackomatic({}, options)

    assert(instance.options.files instanceof RegExp)
    assert(instance.options.files.test('foo.pdf'))
  })

  it('warns when dataLayer is undefined if options.debug is true', function() {
    let stub = sinon.stub(window.console, 'log')

    let options = { debug: 1 }
    let instance = new Trackomatic(trackerMock, options)

    // Trackomatic.boot is normally called in response to domready so
    // we just call it directly in order to simulate this
    instance.boot()

    assert(stub.called)
  })

  it('prepends the prefix to the Category field of tracking calls if options.prefix is not empty', function() {
    let options = { prefix: 'test' }
    let instance = new Trackomatic(trackerMock, options)

    // Trackomatic.boot is normally called in response to domready so
    // we just call it directly in order to simulate this
    let prefixed = instance.prefix({ category: 'Category' })

    assert.equal(prefixed.category, 'test - Category')
  })
})

import assert from 'assert'
import util from '../util'

describe('util', function() {

  describe('createAnchor()', function() {
    it('creates an anchor node and sets the href', function() {
      let anchor = util.createAnchor('http://foo.com/')
      let proto = Object.prototype.toString.call(anchor)

      assert.equal(anchor.href, 'http://foo.com/')
      assert.equal(proto, '[object HTMLAnchorElement]')
    })
  })

  describe('getPathname()', function() {
    it('extracts the pathname from a url', function() {
      let pathname = util.getPathname('http://foo.com/bar/baz')
      assert.equal(pathname, '/bar/baz')
    })
  })

  describe('slugify()', function() {
    it('converts a string with spaces into a suitable all lowercase slug', function() {
      let result = util.slugify('fOO bar BAz')
      assert.equal(result, 'foo-bar-baz')
    })

    it('removes non-word characters but leaves numbers intact', function() {
      let result = util.slugify('foo *@& !bar. baz')
      assert.equal(result, 'foo-bar-baz')
    })

    it('converts double hyphens to single hyphens and prunes hypens from the start and end', function() {
      let result = util.slugify('------foo--bar baz-')
      assert.equal(result, 'foo-bar-baz')
    })
  })

  describe('callOnce()', function() {
    it('returns a wrapped function that is guarded from being invoked more than once', function() {
      let stub = sinon.stub()
      let wrapped = util.callOnce(stub)

      wrapped()
      wrapped()
      wrapped()

      assert(stub.calledOnce)
    })
  })
})

let provide       = require('./provide')
let shouldProvide = require('./shouldProvide')
let Trackomatic   = require('./trackomatic')

// Only provide trackomatic to IE9+
if (shouldProvide) {
  provide('trackomatic', Trackomatic)
}

// IE9+ equivalent to jQuery.one(..)
export default function once(node, evName, callback) {
  let fn = (e) => {
    callback(e)
    node.removeEventListener(evName, fn)
  }

  node.addEventListener(evName, fn, false)
}

/**
 * Plugin base class
 **/
class Plugin {
  /**
   * Constructor for Plugin base class
   *
   * @param { Object } instance - Instance of Trackomatic
   * @returns { Void }
   **/
  constructor(instance) {
    this.__trackomatic__ = instance
    this.install()
  }

  /**
   * Passes params along to the instance of Trackomatic
   *
   * @param   { Object } params - The parameters to be tracked
   * @returns { Void }
   **/
  track(params) {
    this.__trackomatic__.track(params)
  }

  /**
   * Boostrap function implemented by subclasses
   *
   * @abstract
   **/
  install() {}
}

export default Plugin

const BasePlugin = require('../plugin')

/**
 * Tracks JavasSript errors with message and line number
 *
 * Should use exception tracking in next version:
 * https://developers.google.com/analytics/devguides/collection/analyticsjs/exceptions
 *
 * @extends { Plugin }
 */
class ErrorReportingPlugin extends BasePlugin {

  /**
   * The setup function for this plugin
   *
   * @returns { Void }
   */
  install() {
    this.oldonerror = window.onerror

    // this is prone to being replaced or overwritten
    // by other libraries present in the page
    // TODO: better error tracking
    window.onerror = (msg, url, line) => {
      if (this.oldonerror) {
        this.oldonerror.apply(this, arguments)
      }

      this.track({
        category : 'JavaScript Error',
        action   : msg,
        label    : `${ url } @ Line ${ line }`
      })
    }
  }
}

export default ErrorReportingPlugin

const BasePlugin = require('../plugin')
const once       = require('../once')

/**
 * Tracks what the first interaction mode is (mouse, touch, keyboard)
 *
 * @extends { Plugin }
 */
class FirstInputPlugin extends BasePlugin {

  /**
   * The setup function for this plugin
   *
   * @return { Void }
   */
  install() {
    this.firstInputRecorded = false

    once(window, this.getMouseEvent(), this.onMouseInput.bind(this))
    once(window, 'touchstart',         this.onTouchInput.bind(this))
    once(window, 'keydown',            this.onKeyboardInput.bind(this))
  }

  /**
   * Returns the proper name of the mouse event for this environment
   *
   * @return { String } - the name of the mouse event for this browser
   **/
  getMouseEvent() {
    let event = 'mousedown'

    if (window.PointerEvent) {
      event = 'pointerdown'
    } else if (window.MSPointerEvent) {
      event = 'MSPointerDown'
    }

    return event
  }

  /**
   * Callback for mouse input events
   *
   * @param  { Object } e - the event object
   * @return { Void }
   **/
  onMouseInput(e) {
    this.recordFirstInput('mouse', this.__trackomatic__.util.keyCode(e))
  }

  /**
   * Callback for touch input events
   *
   * @param  { Object } e - the event object
   * @return { Void }
   **/
  onTouchInput(e) {
    this.recordFirstInput('touch', this.__trackomatic__.util.keyCode(e))
  }

  /**
   * Callback for keyboard input events
   *
   * @param  { Object } e - the event object
   * @return { Void }
   **/
  onKeyboardInput(e) {
    e = e || window.event
    let code      = this.__trackomatic__.util.keyCode(e)
    let blacklist = this.__trackomatic__.config.EXCLUDED_KEYS

    /**
     * Intent is to filter out keys such as SPACE, UP, DOWN, etc.
     * that a keyboard user might initially use in place of scrolling
     */
    let shouldRecordKey = true

    if (e.metaKey || e.ctrlKey || e.altKey) {
      shouldRecordKey = false
    }

    for (let key in blacklist) {
      if (code === blacklist[key]) {
        shouldRecordKey = false
      }
    }

    if (shouldRecordKey) {
      // track the first keycode
      this.track({
        category : 'First Input',
        action   : 'First Keycode',
        label    : code
      })

      // also attempt to track this as the first input
      this.recordFirstInput('keyboard', code)
    }
  }

  /**
   * Tracks the first input used. Does nothing after the first call.
   *
   * @param { String } inputType - The type of interaction ('click', 'touchstart', etc.)
   * @param { String } keyCode   - The code describing which key was pressed
   */
  recordFirstInput(inputType, keyCode) {
    if (!this.firstInputRecorded) {
      this.track({
        category : 'First Input',
        action   : inputType,
        label    : keyCode
      })

      this.firstInputRecorded = true
    }
  }
}

export default FirstInputPlugin

export default {
  firstInputPlugin       : require('./firstInputPlugin'),
  errorReportingPlugin   : require('./errorReportingPlugin'),
  linkTrackingPlugin     : require('./linkTrackingPlugin'),
  viewportTrackingPlugin : require('./viewportTrackingPlugin')
}

const BasePlugin = require('../plugin')

/**
 * Tracks link clicks
 *
 * @extends { Plugin }
 */
class LinkTrackingPlugin extends BasePlugin {

  /**
   * The setup function for this plugin
   *
   * @returns { Void }
   */
  install() {
    document.documentElement.addEventListener('click', this.onLinkClick.bind(this), false)
  }

  /**
   * Callback for link click events
   *
   * @param   { Object } e - the event object
   * @returns { Void }
   **/
  onLinkClick(e) {
    const link = this.__trackomatic__.util.getLink(e.target || e.srcElement)

    if (link) {
      const data = this.getVisitData(e, link)

      if (data) {
        this.interceptVisit(e, link, data)
      }
    }
  }

  /**
   * Extracts tracking parameters from a clicked link
   *
   * @param   { Object } e    - the event object
   * @param   { Node }   link - the node that was clicked
   * @returns { Object }      - the extracted category, action, and label for this link
   **/
  getVisitData(e, link) {
    const { util, config, options } = this.__trackomatic__

    const url = link.href

    // return if no href
    if (!url) return

    const keyCode    = util.keyCode(e)
    const rightClick = keyCode === config.KEYS.RIGHT_CLICK
    const metaKey    = e.ctrlKey || e.metaKey || e.altKey

    // unless right click or a modifier key is pressed, return the visit data
    if (!rightClick && !metaKey) {
      const extractedAttributes = this.extractAutoTrackingAttributes(url)

      if (extractedAttributes) {
        const map = { ...extractedAttributes, label: url }
        const trackomaticAttributes = link.getAttribute('data-trackomatic')

        if (trackomaticAttributes) {
          const [category, action, label] = trackomaticAttributes.split(options.delimiter)

          map.category = category || map.category
          map.action   = action   || map.action
          map.label    = label    || map.label
        }

        return map
      }
    }
  }

  /**
   * Extracts a tracking category and action based on the url of a clicked link
   *
   * @param   { String }        url - The href of the clicked link
   * @returns { Void | Object }     - Extracted parameters based on matching of url
   **/
  extractAutoTrackingAttributes(url) {
    const { util, config, options } = this.__trackomatic__
    const differentHost = (util.getHost(url) !== util.getHost(window.location.href))

    if (url.match(options.files)) {
      return { category: 'File Click', action: 'click' }
    } else if (url.match(config.REGEX.MAILTO_HREF)) {
      return { category: 'Mailto Click', action: 'click' }
    } else if (url.match(config.REGEX.TEL_HREF)) {
      return { category: 'Telephone Click', action: 'click' }
    } else if (differentHost) {
      if (url.match(options.networks)) {
        return { category: 'Social Media Click', action: 'click' }
      } else {
        return { category: 'Site Exit', action: util.getHost(url) }
      }
    }
  }

  /**
   * Reports tracking data for the clicked link and then redirects to the link url.
   *
   * @param   { Object } e      - The event object
   * @param   { Node }   link   - The clicked link
   * @param   { Object } params - Category, action, and label
   * @returns { Void }
   **/
  interceptVisit(e, link, { category, action, label }) {
    const { config, options } = this.__trackomatic__

    const delay    = Math.min(options.redirectDelay, config.MAX_REDIRECT_DELAY)
    const redirect = this.createRedirect(link)

    // track visit
    this.track({ category, action, label, hitCallback : redirect })

    // in case the GA hitCallback doesn't fire, redirect
    // to the clicked link after the configured delay
    setTimeout(redirect, delay)

    // prevent the default link click behavior (redirect)
    e.preventDefault()
  }

  /**
   * Creates a function that will redirect to the given url or if dev/debug mode
   * is on this function will return a noop. The redirect function is protected
   * by callOnce which prevents it from being called multiple times.
   *
   * @param   { Node }            link - The clicked link
   * @returns { Void | Function }      - Protected redirect function
   **/
  createRedirect(link) {
    const { util, options } = this.__trackomatic__

    if (__DEV__ && options.debug) {
      return util.noop
    }

    // ensure that this redirect callback can be called no more than once
    return util.callOnce(util.createNavigationHandler(link))
  }

}

export default LinkTrackingPlugin

const BasePlugin = require('../plugin')

/**
 * Viewport tracking and reporting plugin
 *
 * @extends { Plugin }
 */
class ViewportTrackingPlugin extends BasePlugin {

  /**
   * The setup function for this plugin
   *
   * @return { Void }
   */
  install() {
    let { util } = this.__trackomatic__

    this.viewportSize  = util.getViewportSize()
    this.viewportRatio = util.getViewportRatio(this.viewportSize)
    this.simpleWidth   = util.roundXtoY(this.viewportSize.width, 100)
    this.simpleHeight  = util.roundXtoY(this.viewportSize.height, 100)

    // track width
    this.track({
      category : 'Viewport',
      action   : 'Initial Width',
      label    : String(this.simpleWidth)
    })

    // track height
    this.track({
      category : 'Viewport',
      action   : 'Initial Height',
      label    : String(this.simpleHeight)
    })

    // track ratio
    this.track({
      category : 'Viewport',
      action   : 'Initial Ratio',
      label    : String(this.viewportRatio)
    })

    // Set up event listeners for viewport tracking
    this.listen()
  }

  /**
   * For browsers that aren't iDevices or Android phones,
   * listen to resize events and update dimensions in response.
   *
   * For all browsers listen to orientationchange events
   * and update dimensions in response
   *
   * @return { Void }
   */
  listen() {
    window.addEventListener(
      'orientationchange',
      this.__trackomatic__.util.debounce(this.trackViewportChanges.bind(this), 1000),
      false
    )

    // if NOT a mobile platform
    if (!this.__trackomatic__.config.MOBILE_PLATFORM) {
      window.addEventListener(
        'resize',
        this.__trackomatic__.util.debounce(this.trackViewportChanges.bind(this), 1000),
        false
      )
    }
  }

  /**
   * Updates viewportSize and reports relative changes to GA
   * in response to window resize or orientation changes
   *
   * @return { Void }
   */
  trackViewportChanges() {
    let size    = this.__trackomatic__.util.getViewportSize()
    let changes = this.calculateViewportChanges(size, this.viewportSize)

    // Track height changes
    if (changes.height) {
      this.track({
        category : 'Viewport',
        action   : 'Resize Height',
        label    : changes.height
      })
    }

    // Track width changes
    if (changes.width) {
      this.track({
        category : 'Viewport',
        action   : 'Resize Width',
        label    : changes.width
      })
    }

    this.viewportSize = size
  }

  /**
   * Updates viewportSize and reports relative changes to GA
   * in response to window resize or orientation changes
   *
   * @param { Object }   p - The "previous" viewport size
   * @param { Obejct }   n - The "next" viewport size
   * @return { Object }   - Relative width and height changes
   */
  calculateViewportChanges(p, n) {
    return {
      height : p.height > n.height ? 'taller' : p.height < n.height ? 'shorter'  : null,
      width  : p.width  > n.width  ? 'wider'  : p.width  < n.width  ? 'narrower' : null
    }
  }

}

export default ViewportTrackingPlugin

/**
 * Provides a plugin name and constructor function to analytics.js.
 * Note: This function works even if the site has customized the GA global identifier.
 *
 * @param { String }   pluginName        - The name of the plugin to provide to GA
 * @param { Function } pluginConstructor - A constructor function for the provided plugin
 */
export default function providePlugin(pluginName, pluginConstructor) {
  var ga = window[window['GoogleAnalyticsObject'] || 'ga']

  if (ga) {
    ga('provide', pluginName, pluginConstructor)
  }
}

/**
 * These requirements define the class of browsers Trackomatic supports
 **/
export default (
  'querySelector' in document &&
  'localStorage' in window &&
  'addEventListener' in window
)

export default {
  category: null,
  action: null,
  label: null,
  value: 0,
  nonInteraction: 1,
  hitType: 'event',
  hitCallback: () => {}
}

const ready   = require('domready')
const plugins = require('./plugins')
const config  = require('./config')
const util    = require('./util')

const defaultTrackParams = require('./trackParams')

/**
 * When called with `new`, creates a new instance of the Trackomatic analytics class
 */
class Trackomatic {
  /**
   * Trackomatic class constructor
   *
   * @param  { Object } tracker - Tracker passed in by Google Analytics
   * @param  { Object } options - Trackomatic configuration object
   * @return { Void }
   **/
  constructor(tracker, options={}) {
    window._trackomatic = window._trackomatic || this

    this.util    = util
    this.config  = config
    this.tracker = tracker
    this.options = this.prepare({ ...config.DEFAULTS, ...options })

    ready(this.boot.bind(this))
  }

  /**
   * Prepares the Trackomatic options by converting `options.networks`
   * and `options.files` from Strings or Array<String> into RegExp
   *
   * @param  { Object } options - Trackomatic's options object
   * @return { Object }         - The prepared options object
   **/
  prepare(options) {
    return {
      ...options,
      networks : util.regexify(options.networks),
      files    : util.regexify(options.files)
    }
  }

  /**
   * Loads all of Trackomatic's plugins
   *
   * @return { Void }
   **/
  boot() {
    if (__DEV__ && this.options.debug) {
      console.log(
        'Loaded trackomatic on tracker ' + this.tracker.get('name') +
        ' with the config object ' + JSON.stringify(this.options)
      )

      // window.dataLayer is provided by GTM
      if (typeof dataLayer === typeof undefined) {
        console.warn(
          'Warning: Trackomatic could not find the global \
          "dataLayer" object from GTM. Did you load GTM first?'
        )
      }
    }

    for (let key in plugins) {
      new plugins[key](this)
    }
  }

  /**
   * Sends track parameters to the configured Google Analytics account via
   * the tracker that was passed to the Trackomatic constructor
   *
   * @param  { Object } params - Object of keys and values to send to GA/GTM
   * @return { Void }
   **/
  track(params) {
    params = this.prefix({ ...defaultTrackParams, ...params })

    this.notifyGA(params)
    this.notifyGTM(params)
  }

  /**
   * When given a trackomatic tracking object, this function will add the
   * configured prefix to the `category` property
   *
   * @param  { Object } params - Tracking object to be prefixed
   * @return { Object }        - The tracking object with prefixed properties
   **/
  prefix({ category, ...params }) {
    const { prefix } = this.options
    return { ...params, category: `${ prefix } - ${ category }` }
  }

  /**
   * Provided `window.dataLayer` exists, `notifyGTM` will push values to it
   *
   * @param  { Object } params - Object of keys and values to send to GTM
   * @return { Void }
   **/
  notifyGTM(params) {
    // extract params that shouldn't be sent to GTM
    const { nonInteraction, hitType, hitCallback, ...GTMParams } = params
    const GTMLoaded = (typeof dataLayer !== typeof undefined)

    if (__DEV__ && this.options.debug) {
      console.log('dataLayer.push(..): ')
      console.log(JSON.stringify(GTMParams, null, '  '))
      return
    }

    GTMLoaded && dataLayer.push(GTMParams)
  }

  /**
   * Sends track parameters to the configured Google Analytics account via
   * the tracker that was passed to the Trackomatic constructor
   *
   * @param  { Object } params - Object of keys and values to send to GTM
   * @return { Void }
   **/
  notifyGA(params) {
    let GAParams = [
      params.hitType,
      params.category,
      params.action,
      params.label,
      params.value,
      {
        nonInteraction : params.nonInteraction,
        hitCallback    : params.hitCallback
      }
    ]

    if (__DEV__ && this.options.debug) {
      console.log('tracker.send(..): ')
      console.log(JSON.stringify(GAParams, null, '  '))
      return
    }

    this.tracker.send(...GAParams)
  }
}

export default Trackomatic

var config = require('./config')

/**
 * Utility functions
 **/
const util = {
  noop() {},

  keyCode(e) {
    e = e || window.event
    return e.which || e.keyCode
  },

  /**
   * Allows easy extraction of domain, pathname, etc off of a full URL.
   * @param { String } href - the url to parse
   */
  createAnchor(href) {
    var target = document.createElement('a')
    target.href = href
    return target
  },

  /**
   * A small wrapper for `createAnchor` that returns just the `pathname`
   * @param { String } href - the url to parse
   */
  getPathname(href) {
    // normalize the pathname, since IE omits the leading slash.
    return href && this.createAnchor(href).pathname.replace(config.REGEX.LEADING_SLASH, '/')
  },

  /**
   * Capitalizes the first letter of a string. Useful for combining inconsistent data sources.
   * @param { String } string - the string to capitalize
   */
  capitalize(string) {
    return string.charAt(0).toUpperCase() + string.slice(1).toLowerCase()
  },

  /**
   * Turns strings into slugs - useful for combining inconsistent data sources
   *
   * @param  { String } text - the string to slugify
   * @return { String }      - the slugified string
   */
  slugify(text) {
    return text.toString()
      .toLowerCase()
      .replace(config.REGEX.SPACES, '-')           // Replace spaces with -
      .replace(config.REGEX.NON_WORD, '')          // Remove all non-word chars
      .replace(config.REGEX.MULTIPLE_HYPHENS, '-') // Replace multiple - with single -
      .replace(config.REGEX.LEADING_HYPHENS, '')   // Trim - from start of text
      .replace(config.REGEX.TRAILING_HYPHENS, '')  // Trim - from end of text
  },

  /**
   * Reads cookies.
   */
  readCookie() {
    return (config.REGEX.COOKIE.exec(document.cookie) || []).pop()
  },

  /**
   * Writes cookies. This function is a no-op if `_trackomatic.options.nocookie` is `true`.
   * @param  { String } name  - the name for the cookie value
   * @param  { Mixed }  value - the value to be stored as a cookie
   * @param  { Number } days  - the number of days to store the cookie for
   * @return { Void }
   */
  createCookie(name, value, days) {
    if(_trackomatic.options.nocookie) return

    var expires = days ? ' expires=' + (new Date(days * 864E5 + (new Date()).getTime())).toGMTString() : ''
    document.cookie = `${ name }=${ value }${ expires }; path=/; domain=${ document.location.hostname }`
  },

  /**
   * Debounces function calls.
   */
  debounce(func, wait, immediate) {
    var timeout

    return function() {
      var context = this
      var args = arguments

      var later = function() {
        timeout = null
        if (!immediate) func.apply(context, args)
      }

      var callNow = immediate && !timeout

      clearTimeout(timeout)
      timeout = setTimeout(later, wait)

      if (callNow) func.apply(context, args)
    }
  },

  /**
   * Gets the viewport size.
   */
  getViewportSize() {
    if (typeof window.innerWidth !== 'undefined') {
      return {
        width  : window.innerWidth,
        height : window.innerHeight
      }
    } else if (typeof document.documentElement !== 'undefined'
        && typeof document.documentElement.clientWidth !== 'undefined'
        && document.documentElement.clientWidth !== 0) {
      return {
        width  : document.documentElement.clientWidth,
        height : document.documentElement.clientHeight
      }
    } else {
      return {
        width  : document.body.clientWidth,
        height : document.body.clientHeight
      }
    }
  },

  getViewportRatio(size) {
    return (size.width / size.height).toPrecision(2)
  },

  /**
   * Rounds numbers.
   */
  roundXtoY(x, y) {
    var upper = Math.ceil(x/y) * y
    var lower = Math.floor(x/y) * y

    return (x - lower < upper - x) ? lower : upper
  },

  createNavigationHandler(link) {
    return function() {
      if (link.target) {
        window.open(link.href, link.target)
      } else {
        window.location = link.href
      }
    }
  },

  getHost(url) {
    return url.replace(config.REGEX.HTTP_PROTOCOL, '').split('/')[0]
  },

  getLink(node) {
    if (node === document.documentElement) {
      return false
    } else if (node.tagName.toLowerCase() === 'a') {
      return node
    } else {
      return this.getLink(node.parentNode)
    }
  },

  getURLParams() {
    let map = {}
    location.href.replace(config.REGEX.QUERY_PARAMS, (m, k, v) => map[k] = v)
    return map
  },

  isArray(item) {
    return Object.prototype.toString.call(item) === '[object Array]'
  },

  regexify(input) {
    input = this.isArray(input) ? input : [input]
    return new RegExp(input.map(s => s.replace(/\./g, '\\.')).join('|'), 'i')
  },

  /**
   * Wraps a function and guards it from being invoked more than once
   *
   * @param  { Function } fn - the function to guard
   * @return { Function }    - the wrapped function
   */
  callOnce(fn) {
    let called = false

    return function callOnceWrappedFunction() {
      if (called) return
      fn()
      called = true
    }
  }
}

